import React from "react"
import axios from 'axios'
import Dropdown from 'react-dropdown'
import 'react-dropdown/dropdownStyle.css'

const options = [
  'Profile', 'Record', 'Generator', 'Settings', 'Feedback'
]
class Home extends React.Component {
	simpleSearch = (event) => {
    	event.preventDefault()
    	const input = event.target.elements.searchBar.value
		if (input === "") {
			return
		}
		axios.post('api', {input})
		.then(res => {
			this.props.changeDisplayedTracks(res)
		})

	}
	createNewTrack = (event) => {
    	event.preventDefault()
	}
	dropdownClick = (event) => {
		console.log("dropdown option clicked")
		this.props.changeScreen(event)
	}
	render() {
		return (
			<div>
				This is the home screen.
				<form onSubmit = {this.simpleSearch} id = "searchButton">
					<input id = "searchBar" placeholder = "type username, track..." />
					<button>search</button>
				</form>
				<form onSubmit = {this.props.changeScreen} id = "switchToAdvancedSearch">
					<button>Try Advanced Search!</button>
				</form>
				<form onSubmit = {this.createNewTrack} id = "createNewButton">
					<button>+</button>
				</form>
				{this.props.error && <div>error!</div>}
				<div id = "createNewText">
				    Create New Track
				</div>
				<Dropdown id = "dropdown" options = {options} onChange = {this.dropdownClick} placeholder = "Select an option" />
			</div>
		)
	}
}

export default Home
