import React from "react"
import './App.css';
import Home from "./components/Home"
import AdvancedSearch from "./components/AdvancedSearch"

class App extends React.Component{
    state = {
        tracks: [],
        curPage: "home",
        error: false
    }
    componentDidMount() {
        fetch("/")
            .then(res => res.json)
            .then(res => this.setState({tracks: res}))
            .then(res=>console.log(this.state.tracks))
    }
    changeScreen = (event) => {
        if (event.value !== undefined) {
            if (event.value === "Profile") {
                console.log("profile clicked")
            }
            return
        }
        event.preventDefault()
        if (event.target.id === "switchToAdvancedSearch") {
            this.setState({curPage: "advancedSearch",
            error: false})
        }
        else if (event.target.id === "switchToHome") {
            this.setState({curPage: "home",
            error: false})
        }
    }
    changeDisplayedTracks = (res) => {
        this.setState({tracks: [{trackid: 10, username: "bear"}]})
    }
    render() {
        return (
            <div>
                <div id = "existingTracks">
                    {this.state.tracks.map(track => <li key = {track.trackid}>{track.trackid}: {track.username}</li>)}
                </div>
                {this.state.curPage === "home" && 
                 <Home changeScreen = {this.changeScreen} tracks = {this.state.tracks} changeDisplayedTracks = {this.changeDisplayedTracks} error = {this.state.error} />}
                {this.state.curPage === "advancedSearch" && 
                 <AdvancedSearch changeScreen = {this.changeScreen} error = {this.state.error} />}
            </div>
        )
    }
}

export default App
